import React, { useState } from 'react';

export default function Categories({items, onClickItem}) {
    useStat
    return (
           <div className="categories">
              <ul>
                <li className="active">Все</li>
                {items.map(item => <li onClick={() => onClickItem(item)} key={item}>{item}</li>)}
              </ul>
            </div> 
    )
}
